import React from 'react';
import './Todo.css';

export class Todo extends React.Component {

    constructor(props) {
        super(props);

        this.state = { 
            checked : false 
        };

        this.delete = this.delete.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    delete() {
        this.props.deleteTodo(this.props.idx);
        const todo = document.getElementById((this.props.idx));
        todo.style.backgroundColor = 'white';
        
    }

    handleChange() {
        this.setState({ checked : true })
        this.markChecked();
    }

    markChecked() {
        const todo = document.getElementById((this.props.idx));
        todo.style.backgroundColor = 'green';
    }


    render() {

        return (
            <div id={(this.props.idx)} className="Todo" onClick={this.handleChange}>
                <div className="description">
                    <p>{this.props.todo}</p>
                </div>
                <div className="buttons">
                    <div className="bin" onClick={this.delete} >
                        <svg xmlns="http://www.w3.org/2000/svg" width="22" height="22" fill="currentColor" class="bi bi-x-circle" viewBox="0 0 16 16">
                            <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"/>
                            <path d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708z"/>
                        </svg>
                    </div>
                </div>
            </div>
        )
    }
 }
