import React from 'react'
import './Todo.css'

export class Todo extends React.Component {

    constructor(props) {
        super(props);
        this.getIdx = this.getIdx.bind(this);
    }

    getIdx() {
        this.props.deleteTodo(this.props.idx);
    }

    deleteAnimation() {
        const todo = document.getElementById(this.props.idx)
    }

    render() {
        return (
            <div id={this.props.idx} className="Todo">
                <div className="description">
                    <p>{this.props.todo}</p>
                </div>
                <div className="buttons">
                    <button className="btn" onClick={this.getIdx} >Del</button>
                </div>
            </div>
        )
    }
 }
