import React from 'react'
import './Todo.css'

export class Todo extends React.Component {

    constructor(props) {
        super(props);
        this.getIdx = this.getIdx.bind(this);
    }

    getIdx() {
        this.props.deleteTodo(this.props.idx);
    }

    render() {
        return (
            <div className="Todo">
                <h4>{this.props.todo}</h4>
                <button onClick={this.getIdx} >Del</button>
            </div>
        )
    }
 }
