import React from 'react'
import './Todo.css'

export class Todo extends React.Component {

    constructor(props) {
        super(props);
        this.getIdx = this.getIdx.bind(this);
    }

    getIdx() {
        this.props.deleteTodo(this.props.idx);
    }

    render() {
        return (
            <div className="Todo">
                <div className="description">
                    <p>{this.props.todo}</p>
                </div>
                <div className="buttons">
                    <button class="btn btn-primary" onClick={this.getIdx} >Del</button>
                </div>
            </div>
        )
    }
 }
